func maximumTime(time string) string {
    result := ""
	Q := "?"

	timeSplit := strings.Split(time, ":")
	hourStr := timeSplit[0]
	if hourStr == "??" {
		result += "23"
	} else if string(hourStr[0]) == Q {
		onesStr := string(hourStr[1])
		if onesStr == "0" || onesStr == "1" || onesStr == "2" || onesStr == "3" {
			result += "2"
		} else {
			result += "1"
		}

		result += onesStr
	} else if string(hourStr[1]) == Q {
		tensStr := string(hourStr[0])
		result += tensStr
		if tensStr == "2" {
			result += "3"
		} else {
			result += "9"
		}
	} else {
		result += hourStr
	}

	result += ":"

	minuteStr := timeSplit[1]
	if string(minuteStr[0]) == Q {
		result += "5"
	} else {
		result += string(minuteStr[0])
	}

	if string(minuteStr[1]) == Q {
		result += "9"
	} else {
		result += string(minuteStr[1])
	}

	return result
}
