class Solution {
public:
    vector<int> zalgo(string &s){
        int n = s.length();
        vector<int> z(n, 0);
        int left = 0, right = 0;
        for(int i = 1; i < n; i++){
            if(i > right){
                left = right = i;
                while(right < n && s[right] == s[right - left]){
                    right++;
                }
                z[i] = right - left;
                right--;
            }else{
                int ind = i - left;
                if(z[ind] + i <= right){
                    z[i] = z[ind];
                }else{
                    left = i;
                    while(right < n && s[right] == s[right - left]){
                        right++;
                    }
                    z[i] = right - left;
                    right--;
                }
            }
        }
        return z;
    }
    string longestPrefix(string s) {
        int n = s.length();
        vector<int> z = zalgo(s);
        int ind = n;
        int _max = 0;
        for(int i = 1; i < n; i++){
            if(z[i] + i == n && _max < z[i]){
                _max = z[i];
                ind = i;
            }
        }
        if(ind == n)return "";
        return s.substr(ind, n - ind);
    }
};
