class Solution:
    def longestDiverseString(self, a: int, b: int, c: int) -> str:
        res = ""
        maxHeap = []
        for count, char in [(-a,'a'), (-b,'b'), (-c,'c')]:
            if count != 0: heappush(maxHeap, (count, char))

        while maxHeap:
            count, char = heappop(maxHeap)

            if len(res) > 1 and res[-2] == res[-1] == char:
                if not maxHeap: break
                count2, char2 = heappop(maxHeap)
                res += char2
                count2 += 1
                if count2 != 0: heappush(maxHeap, (count2, char2))
            else:
                res += char
                count += 1

            if count != 0: heappush(maxHeap, (count, char))

        return res
