class Solution {
public:
    bool check(string &s){
        map<int, int> mp;
        for(auto i : s)mp[i]++;
        for(auto i : mp){
            if(i.first >= 'a' && i.first <= 'z'){
                if(mp[i.first - 'a' + 'A'] == 0)return false;
            }else{
                if(mp[i.first - 'A' + 'a'] == 0)return false;
            }
        }
        return true;
    }
    string longestNiceSubstring(string s) {
        string ans = "";
       for(int i = 0; i < s.length(); i++){
           string t = "";
           for(int j = i; j < s.length(); j++){
               t.push_back(s[j]);
               if(check(t) && t.length() > ans.length()){
                   ans = t;
               }
           }
       }
        return ans;
    }
};
