class Solution {
public:
    int longestBeautifulSubstring(string word) {
        unordered_set<char>st;
        int ans=0;
        int len=0;

        string s="aeiou";
        unordered_map<char,int>mp;
        int cnt=0;
        for(auto it:s) mp[it]=cnt++;

        for(auto it:word){
            if(st.size()==0&&it=='a') st.insert(mp[it]) , len++;

            else if(st.size()&&(st.count(mp[it])||st.count(mp[it]-1))) st.erase(*st.begin()),st.insert(mp[it]),len++;
            else {
                if(st.size()&&*st.begin()==4)
                ans=max(ans,len);

                len=0;

                st.clear();
                if(it=='a') st.insert(mp[it]),len++;
}
        }

          if(st.size()&&*st.begin()==4)
                ans=max(ans,len);


        return ans;
    }


};
