class Solution {
public:
    string findLongestWord(string s, vector<string>& nums) {

        sort(nums.begin(),nums.end());
        string ans="";int len=0;


        for(int i=0;i<nums.size();i++)
        {
           string k=nums[i];
            int c=0;int j=0;int f=0;
            while(j<s.length())
            {
               if(k[c]==s[j])
               {
                   c++;
                   j++;
               }
                else{
                    j++;
                }
                if(c==k.length())
                {
                    f=1;
                    break;
                }
            }
            if(f==1)
            {
                if(c>len)
                {
                    len=c;
                    ans=k;
                }
            }



        }
        return ans;


    }
};
