class Solution {
    public int minSubarray(int[] nums, int p) {

        int n = nums.length;
        int sum = 0;

        for(int x: nums)
            sum = (sum+x)%p;

        int targetMod = sum%p;
        if(targetMod==0)
            return 0;

        Map<Integer,Integer> map = new HashMap();
        map.put(0,-1);

        int pSum = 0;
        int result = n;
        for(int i=0;i<n;i++)
        {
            pSum=(pSum+nums[i])%p;
            int correspondingMod = (pSum-targetMod+p)%p;
            if(map.get(correspondingMod)!=null)
                result=Math.min(result,i-map.get(correspondingMod));
            map.put(pSum,i);
        }

        return result==n? -1: result;
    }
}
