class Solution {
public:
    vector<int> left(vector<int> &arr, int k){
        for(int i = 0; i < k; i++){
            int temp = arr[0];
            arr.erase(arr.begin());
            arr.push_back(temp);
        }
        return arr;
    }
    vector<int> right(vector<int> &arr, int k){
        for(int i = 0; i < k; i++){
            int temp = arr.back();
            arr.pop_back();
            arr.insert(arr.begin(), temp);
        }
        return arr;
    }
    bool areSimilar(vector<vector<int>>& mat, int k) {
        vector<vector<int>> arr = mat;
        for(int i = 0; i < mat.size(); i++){
            if(i%2){
                arr[i] = right(arr[i], k);
            }else{
                arr[i] = left(arr[i], k);
            }
        }
        return arr == mat;
    }
};
