class Solution {
    public int maxDiff(int num) {
        String n=""+num;
        String a= ""+num;

        // maximize number replace first non 9 with 9
        for(var x: n.toCharArray()){
            if(x!='9'){
                a= n.replace(x,'9');
                break;
            }
        }

        String b= ""+num;        
        // minimize number without leading zeros
        for(var x: n.toCharArray()){
            if(x==n.charAt(0) && x!='1'){
                b= n.replace(x,'1');
                break;
            }
            if(x!=n.charAt(0) && x!='0'){
                b= n.replace(x,'0');
                break;
            }
        }
        //System.out.println(a+" "+b);
        return Integer.parseInt(a)-Integer.parseInt(b);
    }
}
