class Solution {
public:
    int maxScore(vector<vector<int>>& grid) {
        int n = grid.size();
        int m = grid[0].size();
        vector<vector<int>> arr = grid;
        for(int i = n - 1; i >= 0; i--){
            for(int j = m - 1; j >=0; j--){
                if(i < n - 1){
                    arr[i][j] = max(arr[i][j], arr[i + 1][j]);
                }
                if(j < m - 1){
                    arr[i][j] = max(arr[i][j], arr[i][j + 1]);
                }
            }
        }
        int ans = -1e9;
        for(int i = 0; i < n; i++){
            for(int j = 0; j < m; j++){
                int _max = -1e9;
                if(i < n - 1){
                    _max = max(_max, arr[i + 1][j]);
                }
                if(j < m - 1){
                    _max = max(_max, arr[i][j + 1]);
                }
                if(_max != -1e9)
                    ans = max(ans, _max - grid[i][j]);
            }
        }
        return ans;
    }
};
