class Solution:
    def maximumGood(self, statements: List[List[int]]) -> int:

        def helper(mask):
            n = len(statements)
            for i in range(n):
                if mask & (1 << i) == 0:
                    continue
                for j in range(n):
                    s, t = statements[i][j], (mask >> j) & 1
                    if s == 2:
                        continue
                    if s ^ t:
                        return False
            return True

        n = len(statements)
        ans = 0
        for mask in range((1 << n) - 1, 0, -1):
            count = mask.bit_count()
            if count <= ans:
                continue
            if helper(mask):
                ans = max(ans, count)
        return ans
