typedef long long int ll;
class Solution {
public:
    bool good(ll k, ll x, ll mid){
        mid++;
        ll pow = 2;
        ll ans = 0;
        for (ll i = 0; i < 60; i++)
        {
            ll sz = pow;
            if ((i + 1) % x == 0)
            {
                ll full = mid / sz;
                ll rem = max(0LL, mid % sz - pow / 2);

                ll tot = (pow / 2) * (full) + rem;
                ans += tot;
            }
            pow *= 2;
        }
        return ans <= k;
    }
    long long findMaximumNumber(long long k, int x) {
        ll lo = 0, hi = 1e15, ans = -1;
        while(lo <= hi){
            ll mid = (lo + hi + 1)/2;
            if(good(k, x, mid)){
                lo = mid + 1;
                ans = mid;
            }else{
                hi = mid - 1;
            }
        }
        return ans;
    }
};
