class Solution {
public:
    int maximumSetSize(vector<int>& nums1, vector<int>& nums2) {
        map<int, int> mp1, mp2;
        int n = nums1.size();
        for(auto i : nums1)mp1[i]++;
        for(auto i : nums2)mp2[i]++;
        int a = mp1.size(), b = mp2.size();
        for(auto i : mp1)a += i.second - 1;
        for(auto i : mp2)b += i.second - 1;
        map<int, int> mp3;
        for(auto i : nums1)mp3[i] = 1;
        for(auto i : nums2)if(mp3[i] == 1)mp3[i] = 2;
        int com = 0;
        for(auto i : mp3)if(i.second == 2)com++;

        int da = min((int)mp1.size() - com, n/2);
        int db = min((int)mp2.size() - com, n/2);
        return min(da + db + com, n);
    }
};
