class Solution {
public:
    int minCostConnectPoints(vector<vector<int>>& points) {
        int p = 0, res = 0;
        vector<int> dp(points.size(), INT_MAX);
        dp[0] = -1;
        for (int i = 1; i < points.size(); i++) {
            for (int j = 0; j < points.size(); j++) {
                if (dp[j] == -1) continue;
                dp[j] = min(dp[j], f(points[p], points[j]));
            }
            int m = INT_MAX, k = -1;
            for (int j = 0; j < points.size(); j++) {
                if (dp[j] == -1) continue;
                if (dp[j] < m) {
                    m = dp[j];
                    k = j;
                }
            }
            res += dp[k];
            dp[k] = -1;
            p = k;
        }
        return res;
    }

private:
    inline int f(vector<int>& p, vector<int>& q) {
        return abs(p[0] - q[0]) + abs(p[1] - q[1]);
    }
};
