class Solution:
    def minProductSum(self, nums1: List[int], nums2: List[int]) -> int:

        nums1.sort()
        nums2_sort = sorted(nums2, reverse=True)
        sum_val = 0
        for i in range(0,len(nums1)):
            sum_val = sum_val + nums1[i]*nums2_sort[i]

        return sum_val
