class Solution {
public:
    vector<int> minDifference(vector<int>& nums, vector<vector<int>>& queries) {
        vector<vector<int>> prefix(101, vector<int>(nums.size()+1, 0));
        for(int i = 0; i < nums.size(); i++){
            for(int j = 1; j <= 100; j++){
                prefix[j][i+1] = prefix[j][i] + (nums[i] == j);
            }
        }
        vector<int> ans;
        for(auto& q: queries){
            int l = q[0], r = q[1];
            int prev = -1;
            int mn = INT_MAX;
            for(int i = 1; i <= 100; i++){
                if(prefix[i][r+1] - prefix[i][l] > 0){
                    if(prev != -1){
                        mn = min(mn, i - prev);
                    }
                    prev = i;
                }
            }
            if(mn == INT_MAX){
                ans.push_back(-1);
            }
            else{
                ans.push_back(mn);
            }
        }
        return ans;
    }
};
