class Solution {
    public long[] minimumCosts(int[] regular, int[] express, int expressCost) {
        int N = regular.length;
        long[] res = new long[N];                
        long[] dp = new long[2];
        dp[0] = 0;
        dp[1] = expressCost;


        for (int i = 1; i <= N; i++) {            
            long[] tempDp = new long[2];

            tempDp[0] = Math.min( regular[i-1] + dp[0], regular[i-1] + dp[1]  );
            tempDp[1] = Math.min( expressCost + express[i-1] + dp[0], express[i-1] + dp[1]  );

            res[i-1] = Math.min(tempDp[0],tempDp[1]);
            dp = tempDp;
        }

        return res;
    }
}
