class Solution {
public:
    int minimumMoves(string s) {
        int count=0;
        for(int i=0;i<s.length();){
            if(s[i]=='X')
            {count++;
                i=i+3;
                 // if(i>=s.length())
                 //                break;
            }
            else i++;
        }
        if(count==s.size())
            return 0;
        else
            return (count);

    }
};
