class Solution {
public:
    int minimumAddedCoins(vector<int>& arr, int target) {
        int n = arr.size();
        sort(arr.begin(), arr.end());
        int curr = 0, ans = 0, ind = 0;
        for(int i = 1; i <= target; i++){
            if(curr >= i)continue;

            if(ind < n){
                if(curr + 1 == arr[ind]){
                    curr += arr[ind];
                    ind++;
                }else if(curr >= arr[ind]){
                    curr += arr[ind];
                    ind++;
                }else{
                    curr += (curr + 1);
                    ans ++;
                }
            }else{
                curr += (curr + 1);
                ans++;
            }
        }
        return ans;
    }
};
