class Solution:
    def minFlips(self, s: str) -> int:
        s1 = s + s
        n = len(s)
        str1, str2 = "", ""
        for i in range(len(s1)):
            if i%2:
                str1+="1"
                str2+="0"
            else:
                str1+="0"
                str2+="1"
        l = 0
        res, res1, res2 = len(s1), 0, 0
        for r in range(len(s1)):
            if s1[r]!=str1[r]:
                res1+=1
            if s1[r]!=str2[r]:
                res2+=1
            if r-l+1>n:
                if s1[l]!=str1[l]:
                    res1-=1
                if s1[l]!=str2[l]:
                    res2-=1
                l+=1
            if r-l+1==n:
                res = min(res, res1, res2)
        return res
