int dp[100000][2][2];
class Solution {
public:
    int helper(string &hamsters, int ind, bool required, bool p1){
        if(ind == hamsters.size()){
            if(required)return 1e9;
            return 0;
        }
        if(dp[ind][required][p1] != -1)return dp[ind][required][p1];

        if(hamsters[ind] == 'H'){
            if(required){
                return 1e9;
            }else{
                if(p1){
                    return helper(hamsters, ind + 1, false, false);
                }else{
                    return helper(hamsters, ind + 1, true, false);
                }
            }
        }else{
            int tk = 1e9, nt = 1e9;
            if(!required)
                nt = helper(hamsters, ind + 1, false, false);
            tk = 1 + helper(hamsters, ind + 1, false, true);

            return dp[ind][required][p1] = min(tk, nt);
        }
    }
    int minimumBuckets(string hamsters) {
        for(int i = 0; i < hamsters.length(); i++){
            for(int j = 0; j < 2; j++){
                dp[i][j][0] = dp[i][j][1] = -1;
            }
        }
        int ans =  helper(hamsters, 0, false, false);
        if(ans == 1e9)return -1;
        return ans;
    }
};
