class Solution:
    def minNumberOfFrogs(self, croakOfFrogs: str) -> int:
        croaks = {'c':0, 'r':0, 'o':0, 'a':0, 'k':0}
        min_frogs = 0
        frogs = 0

        for c in croakOfFrogs:
            croaks[c] += 1

            # condition for a valid croak
            if not (croaks['c'] >= croaks['r'] >= croaks['o'] >= croaks['a'] >= croaks['k']):
                return -1

            # beginning of a frog croak, add a frog
            if c == 'c':
                frogs += 1
            # end of a frog croak, substract a frog
            elif c == 'k':
                frogs -= 1

            min_frogs = max(min_frogs, frogs)

        return min_frogs if frogs == 0 else -1
