class Solution {
public:
    int minimumOperationsToMakeEqual(int x, int y) {
        if(y >= x)return y - x;
        queue<int> q;
        q.push(x);

        vector<int> vis(1e6, 0);
        vis[x] = 1;

        int ans = 0;
        while(!q.empty()){
            int sz = q.size();
            for(int i = 0; i < sz; i++){
                auto f = q.front();
                q.pop();
                // cout<<f<<endl;
                if(f == y){
                    return ans;
                }

                if(f%11 == 0 && !vis[f/11]){
                    vis[f/11] = 1;
                    q.push(f/11);
                }
                if(f%5 == 0 && !vis[f/5]){
                    vis[f/5] = 1;
                    q.push(f/5);
                }

                if(f > 0 && !vis[f - 1] ){
                    vis[f - 1] = 1;
                    q.push(f - 1);
                }
                if(!vis[f + 1]){
                    vis[f + 1] = 1;
                    q.push(f + 1);
                }
            }
            ans++;
        }
        return -1;
    }
};
