class Solution {
public:
    vector<int> minOperations_newschool(string boxes) {
        int n=boxes.size();
        vector<int>out(n);
        vector<int>left(n);
        vector<int>right(n);

        int count_one=boxes[0]-'0';
        for(int i=1;i<n;i++)
        {
            left[i]+=left[i-1]+count_one;
            count_one+=boxes[i]-'0';
        }

        count_one=boxes[n-1]-'0'; 
        for(int i=n-2;i>=0;i--)
        {
            right[i]+=right[i+1]+count_one;
            count_one+=boxes[i]-'0';
        }

        for(int i=0;i<n;i++)
            out[i]=left[i]+right[i];


        return out;
    }

    vector<int> minOperations(string boxes) {

        return minOperations_newschool(boxes);

    }
};
