class Solution {
public:
    int minimumOperationsToWriteY(vector<vector<int>>& grid) {
        int a = 0, b = 0, c = 0;
        for(auto i : grid)for(auto j : i){
            if(j == 0)a++;
            if(j == 1)b++;
            if(j == 2)c++;
        }
        int n = grid.size();
        int d = 0, e = 0, f = 0;
        for(int i = 0, j = 0; i <= n/2; i++, j++){
            if(grid[i][j] == 0)d++;
            if(grid[i][j] == 1)e++;
            if(grid[i][j] == 2)f++;
        }
        for(int i = 0, j = n - 1; i < n/2; i++, j--){
            cout<<i<<" "<<j<<endl;
            if(grid[i][j] == 0)d++;
            if(grid[i][j] == 1)e++;
            if(grid[i][j] == 2)f++;
        }
        for(int i = n/2 + 1; i < n; i++){
            if(grid[i][n/2] == 0)d++;
            if(grid[i][n/2] == 1)e++;
            if(grid[i][n/2] == 2)f++;
        }
        a-=d;
        b-=e;
        c-=f;
        // cout<<a<<" "<<b<<" "<<c<<" "<<d<<" "<<e<<" "<<f<<endl;
        int ans = 1e9;
        ans = min(ans, e + f + min(a + c, a + b));
        ans = min(ans, d + f + min(b + c, a + b));
        ans = min(ans, d + e + min(b + c, a + c));
        return ans;
    }
};
