class Solution {
    private final Set<Integer> thirties = new HashSet(Arrays.asList(9, 4, 6, 11));

    public int daysBetweenDates(String date1, String date2) {
        int y1 = Integer.valueOf(date1.substring(0,4));
        int y2 = Integer.valueOf(date2.substring(0,4));
        int startYear = Math.min(y1, y2);
        return Math.abs(daysAfterStart(date1, startYear) - daysAfterStart(date2, startYear));
    }

    private int daysAfterStart(String date, int startYear){
        String[] dateArr = date.split("-");
        int year = Integer.valueOf(dateArr[0]), month = Integer.valueOf(dateArr[1]), day = Integer.valueOf(dateArr[2]);
        int days = 365*(year-startYear);
        int firstLeapYear = Integer.MAX_VALUE;

        for(int i = startYear; i < year; i++) {
            if(i%4 == 0) {
                firstLeapYear = i;
                break;
            }
        }
        for (int i = firstLeapYear; i < year; i+=4){
            if (i%100 != 0 || i%400 == 0) days += 1;
        }

        for (int i = 1; i < month; i++){
            if(thirties.contains(i)) days += 30;
            else if (i == 2){
                days += year%4 == 0 && (year%100 != 0 || year%400 == 0) ? 29 : 28;
            }
            else days += 31;
        }
        days+= day;

        return days;
    }

}
