class Solution {
    public int countDistinct(String s) {
        int n = s.length();
        long base = 26;
        int count = 0;

        for (int len = 1; len <= n; len++) {
            long power = 1;
            for (int i = 0; i < len - 1; i++) {
                power *= base;
            }

            HashSet<Long> set = new HashSet<>();
            long hash = 0;
            for (int i = 0; i < n; i++) {
                if (i - len >= 0) {
                    hash -= (s.charAt(i - len) - 'a') * power;
                }

                hash = hash * base + (s.charAt(i) - 'a');
                if (i >= len - 1)
                    set.add(hash);
            }
            count += set.size();
        }
        return count;
    }
}
