class Solution {
    public int numEquivDominoPairs(int[][] dominoes) {
        HashMap<String, Integer> map = new HashMap();
        int sum = 0;
        for(int[] i : dominoes){
            int a = i[0];
            int b = i[1];
            StringBuilder str = new StringBuilder();
            if(a < b ){
                str.append(a+" "+b);

            }
            else{
                str.append(b+" "+a);
            }
            map.put(str.toString(), map.getOrDefault(str.toString(), 0) + 1);
        }
        for(String k : map.keySet()){
            int n = map.get(k);
            sum += ((n*(n-1))/2);
        }
        return sum;

    }
}
