class Solution {
public:
    int setBits(int n){
        int res = 0;
        for(; n; n >>= 1) res += n & 1;
        return res;
    }

    long long countExcellentPairs(vector<int>& nums, int k) {
        int cnt[31] = {}, i, j; long long res = 0LL;
        unordered_set<int> seen;
        for(int& num : nums){
            if(!seen.count(num)){
                cnt[setBits(num)]++; 
                seen.insert(num);
            }
        }
        // for(i = 2; i < 31; i++) cnt[i] += cnt[i - 1]; 
        // for(i = max(0, k - 30); i < 31; i++){
        //     3 30
        // }
        for(i = 1; i <= 30; i++){
            if((i << 1) >= k) res += cnt[i] + cnt[i] * (cnt[i] - 1);
            for(j = i + 1; j <= 30; j++)
                if(i + j >= k) res += cnt[i] * cnt[j] * 2;
        }
        return res;

    }
};
