class Solution {
public:
    bool check(vector<int> &nums, vector<int> & pattern){
        for(int i = 0; i < pattern.size(); i++){
            if(pattern[i] == 1){
                if(nums[i + 1] <= nums[i])return false;
            }else if(pattern[i] == 0){
                if(nums[i + 1] != nums[i])return false;
            }else{
                if(nums[i + 1] >= nums[i])return false;
            }
        }
        return true;
    }
    int countMatchingSubarrays(vector<int>& nums, vector<int>& pattern) {
        int ans = 0;
        for(int i = 0; i < nums.size(); i++){
            vector<int> arr;
            for(int j = i; j < nums.size(); j++){
                arr.push_back(nums[j]);
                if(arr.size() == pattern.size() + 1 && check(arr, pattern)){
                    ans++;
                }
            }
        }
        return ans;
    }
};
