class Solution {
    public int countValidWords(String sentence) {

        String[] arr = sentence.split("\\ ");
        int res=0;
        for(String s : arr){
            if(!s.isEmpty()){
                char[] charr= s.toCharArray();
                int len =charr.length;
                boolean valid=true;
                boolean hypen=false;
                for(int i=0; i<len && valid; i++){
                    if(Character.isDigit(charr[i]) 
                       || (charr[i]!='-' && !Character.isLetter(charr[i]) && i!=len-1)){
                        valid=false;
                        break;
                    }
                    if(charr[i]=='-'){
                        if(hypen || ((i==0 || !Character.isLetter(charr[i-1])) 
                          || (i==len-1 || !Character.isLetter(charr[i+1])))){
                            valid=false;
                            break;
                        }else{
                            hypen=true;
                        }
                    }
                }

               if(valid){
                   res++;
               }           
            }
        }

        return res;
    }
}
