class Solution:
    def minimumSemesters(self, n: int, relations: List[List[int]]) -> int:
        '''
        Make dictionary with key as course number and value as everything you have to take before

        Iterate dictionary and if all prerequisites are taken, add course to and add 
        '''

        preq = {}

        for relation in relations:
            pre = relation[0]
            nextCr = relation[1]

            if nextCr in preq:
                preq[nextCr].append(pre)
            else:
                preq[nextCr] = [pre]


        # print(preq)

        taken = set()



        for courseNum in range(1,n+1):
            if courseNum not in preq:
                taken.add(courseNum)

        if len(taken)==n:
            return 1

        semCount = 1

        # print(len(preq))
        # print('----------')
        # print(taken)

        while semCount<n:
            # print('semCount:',semCount)
            # print('taken:',taken)
            tmpTaken = taken.copy()
            for course in preq:
                # print('course',course)
                if course not in taken:
                    # print('preq[course]:',preq[course])

                    currCourses = preq[course]
                    currIdx = 0
                    curr = True

                    while curr:
                        if currIdx>=len(currCourses) or currCourses[currIdx] not in taken:
                            # print('changing curr to False')
                            curr = False
                            break

                        currIdx+=1


                    # print(currIdx)
                    # print(currCourses)
                    if currIdx >= len(currCourses):
                        # print('adding course:',course)
                        tmpTaken.add(course)
                    else:
                        continue

            semCount+=1
            if len(tmpTaken)==n:
                return semCount
            taken = tmpTaken


        # print('here')
        # print('taken:',taken)
        if len(taken)==n:
            return semCount
        return -1
