# Definition for singly-linked list.
# class ListNode:
#     def __init__(self, val=0, next=None):
#         self.val = val
#         self.next = next
class Solution:
    def plusOne(self, head: ListNode) -> ListNode:
        pseudo_node = ListNode(0)
        pseudo_node.next = head
        last_digit = pseudo_node

        while head:
            if head.val !=9:
                last_digit = head
            head = head.next

        last_digit.val += 1
        last_digit = last_digit.next

        while last_digit:
            last_digit.val = 0
            last_digit = last_digit.next 

        if pseudo_node.val:
            return pseudo_node
        else:
            return pseudo_node.next
