class Solution:
    def primePalindrome(self, n: int) -> int:
        length = len(str(n))
        digits = 9
        if n == 1:
            return 2
        for i in range(length, digits+1):
            end = int('9' *  math.ceil(i/2))
            for num in range(1, end + 1):
                tmp  = str(num)
                if i % 2 == 0:
                    num = int(tmp + tmp[::-1])
                else:
                    num = int(tmp + tmp[:-1][::-1])
                if num >= n and self.isPrime(num):
                    return num








    def isPrime(self, num:int) -> bool:
        for i in range(2, int(num ** 0.5) + 1):
            if num % i == 0:
                return False
        return True
