class Solution {
public:

    void makeStrings(vector<string> &vec,string s){
        string push = "";
        int n = s.size();

        for(int i=0; i<n; i++){
            if(s[i] == ' '){
                vec.push_back(push);
                push = "";
            }else{
                push += s[i];
            }
        }
        vec.push_back(push);
    }

    int lenHelper(vector<string> &vec){
        int ans = 0;

        for(auto str:vec){
            ans = max(ans, (int)str.size());
        }

        return ans;
    }

    void trimRight(string &s){
        int i = s.size()-1;

        while(s[i] == ' '){ 
            i--;
        }

        s = s.substr(0, i+1);
    }

    vector<string> printVertically(string s) {
        vector<string> vec;
        vector<string> res;

        makeStrings(vec, s);
        int maxLen = lenHelper(vec);

        for(int i=0; i<maxLen; i++){
            int n = vec[i].size();
            string smallRes = "";

            for(int j=0; j<vec.size(); j++){
                if(i < vec[j].size()){
                    smallRes += vec[j][i];
                }else{
                    smallRes += " ";
                }
            }

            trimRight(smallRes);
            res.push_back(smallRes);
        }

        return res;
    }
};
