class Solution {
public:
    vector<int> prisonAfterNDays(vector<int>& cells, int n) {
        string cur = "00000000";
        map<int,string> mp;
        int count = 0;
        while(count<14){
            for(int i=1;i<7;i++){
                if(cells[i-1]==cells[i+1]){
                    cur[i]= '1';
                }else{
                    cur[i] = '0';
                }
            }
            cells[0] = cells[7] = 0;
            for(int i=1;i<7;i++){
                cells[i] = cur[i]-'0';
            }
            mp[count++]=cur;
        }
        vector<int> ans;
        for(auto e:mp[(n-1)%14]){
            ans.push_back(e-'0');
        }
        return ans;
    }
};
