class Solution {
public:
    int racecar(int target) {
        queue<pair<int, int>> q;
        unordered_set<string> vis;
        q.push({0, 1});
        vis.insert("0 1");

        int step = 0;
        while (!q.empty()) {
            int size = q.size();
            for (int i = 0; i < size; i++) {
                int pos, speed;
                tie(pos, speed) = q.front();
                q.pop();
                if (pos == target)
                    return step;
                // A
                int pos1 = pos + speed, speed1 = speed * 2;
                string key1 = to_string(pos1) + " " + to_string(speed1);
                if (pos1 > 0 && pos1 < target * 2 && !vis.count(key1)) {
                    q.push({pos1, speed1});
                    vis.insert(key1);
                }
                // R
                int speed2 = (speed > 0 ? -1 : 1);
                string key2 = to_string(pos) + " " + to_string(speed2);
                if (pos > 0 && pos < target * 2 && !vis.count(key2)) {
                    q.push({pos, speed2});
                    vis.insert(key2);
                }
            }
            step++;
        }
        return -1;

    }
};
