class Solution {
public:
    string arrangeWords(string text) {
        vector<pair<pair<int, int>, string>> arr;
        string temp = "";
        int ind = 0;
        for(int i = 0; i < text.length(); i++){
            if(text[i] == ' '){
                arr.push_back({{temp.length(), ind}, temp});
                temp = "";
                ind++;
            }else{
                temp.push_back(tolower(text[i]));
            }
        }
        arr.push_back({{temp.length(), ind}, temp});
        sort(arr.begin(), arr.end(), [](pair<pair<int, int>, string> &a, pair<pair<int, int>, string> &b){
            if(a.first.first != b.first.first)return a.first.first < b.first.first;
            return a.first.second < b.first.second;
        });
        string ans = "";
        for(auto i : arr){
            ans += i.second;
            ans += " ";
        }
        ans.pop_back();
        ans[0] = ans[0] - 'a' + 'A';
        return ans;
    }
};
