class Solution {
public:
    string reformatNumber(string number) {
        string s = "";
        for(auto i : number)if(i >= '0' && i <='9')s += i;
        int n = s.size();
        string ans = "";
        for(int i = 0; i < n;){
            if(n - i > 4){
                ans += s.substr(i, 3) + "-" ;
                i += 3;
            }else if(n - i == 4){
                ans += s.substr(i, 2) + "-" + s.substr(i + 2, 2);
                return ans;
            }else if(n - i == 3){
                ans += s.substr(i, 3);
                return ans;
            }else if(n - i == 2){
                ans += s.substr(i, 2);
                return ans;
            }
        }
        return ans;        
    }
};
