class Solution {

    // https://leetcode.com/problems/remove-all-ones-with-row-and-column-flips-ii/discuss/2064870/DFS-with-bactracking-Java
    public int removeOnes(int[][] grid) {
        return dfs(grid, new HashSet<>(), new HashSet<>());
    }

    int dfs(int[][] grid, Set<Integer> xVisited, Set<Integer> yVisited) {
        int min = Integer.MAX_VALUE;

        for (int i = 0; i < grid.length; i += 1) {
            if (xVisited.contains(i)) continue;
            xVisited.add(i);
            for (int j = 0; j < grid[0].length; j += 1) {
                if (yVisited.contains(j) || grid[i][j] == 0) continue;
                yVisited.add(j);
                min = Math.min(min, 1 + dfs(grid, xVisited, yVisited));
                yVisited.remove(j);
            }
            xVisited.remove(i);
        }
        return min == Integer.MAX_VALUE ? 0 : min;
    }
}
