class Solution {
public:
    int dp[110][110][110];
    vector<int>arr;
    int n;
    int rec(int i,int j,int k)
    {
        if(i>j)
            return 0;
        if(dp[i][j][k]!=-1)
            return dp[i][j][k];
        int cnt=0;
      //  while(i+1<=j && arr[i]==arr[i+1])
       // {
         //   k++;
           // i++;
       // }
        int ans=(k+1)*(k+1)+rec(i+1,j,0);
        for(int m=i+1;m<=j;m++)
        {
            if(arr[i]==arr[m])
                ans=max(ans,rec(i+1,m-1,0)+rec(m,j,k+1));
        }
        return dp[i][j][k]=ans;
    }
    int removeBoxes(vector<int>& boxes) {
        arr=boxes;
        n=arr.size();
        memset(dp,-1,sizeof(dp));
        return rec(0,n-1,0);

    }
};
