class Solution {
    public List<List<Integer>> removeInterval(int[][] intervals, int[] toBeRemoved) {
        List<List<Integer>> res = new ArrayList<>();

        int i = 0;

        while (i < intervals.length && toBeRemoved[0] > intervals[i][1]) {
            res.add(Arrays.stream(intervals[i++]).boxed().collect(Collectors.toList()));
        }

        while (i < intervals.length && intervals[i][0] <= toBeRemoved[1]) {
            if (intervals[i][0] < toBeRemoved[0]) {
                res.add(Arrays.asList(intervals[i][0], toBeRemoved[0]));
            }

            if (intervals[i][1] > toBeRemoved[1]) {
                res.add(List.of(toBeRemoved[1], intervals[i][1]));
            }
            i++;
        }

        while (i < intervals.length) {                                        res.add(Arrays.stream(intervals[i++]).boxed().collect(Collectors.toList()));
        }

        return res;
    }
}
