class Solution {
public:
    vector<int> zalgo(string s){
        int n = s.length();
        vector<int> z(n, 0);
        int left = 0, right = 0;
        for(int i = 1; i < n; i++){
            if(i > right){
                left = right = i;
                while(right < n && s[right] == s[right - left]){
                    right++;
                }
                z[i] = right - left;
                right--;
            }else{
                int ind = i - left;
                if(z[ind] + i <= right){
                    z[i] = z[ind];
                }else{
                    left = i;
                    while(right < n && s[right] == s[right - left]){
                        right++;
                    }
                    z[i] = right - left;
                    right--;
                }
            }
        }
        return z;
    }
