/**
 * @param {string} s
 * @return {number}
 */


var test = (dic) => {
    for (let key in dic) {
        if (dic[key] > 0) return false;
    }
    return true;
}
var balancedString = function(s) {
    let dic = {};
    for (let i = 0; i < s.length; i++) {
        let cur = s[i];
        if (!(cur in dic)) {
            dic[cur] = 0;
        }
        dic[cur] += 1;
    }
    let n = s.length;
    let set = {};
    let large = {};
    for (let key of ['Q', 'W', 'R', 'E']) {
        set[key] = 0;
        large[key] = 0;
    }
    let diff = 0;
    for (let key in dic) {
        if (dic[key] > n / 4) {
            large[key] = dic[key] - n / 4;
            diff += dic[key] - n / 4;
        } 
    }
    let i = 0;
    let j = 0;
    if (diff === 0) return 0;
    let res = Number.MAX_VALUE;

    // console.log(large)
    while (j < s.length) {
        let cur = s[j];
        large[cur] -= 1;

        while (i <= j && test(large)) {
            res = Math.min(res, j - i + 1);
            let prev = s[i];
            large[prev] += 1;
            i += 1;
        }
        j += 1;
    }
    return res;

};
