class Solution:
    def reverseWords(self, s: List[str]) -> None:
        """
        Do not return anything, modify s in-place instead.
        """
        def reverse(s, left, right):
            while left < right:
                s[left], s[right] = s[right], s[left]
                left += 1
                right -= 1

        def reverse_word(s):
            start = 0
            end = 0
            while end < len(s):
                while end < len(s) and s[end] != " ":
                    end += 1
                reverse(s, start, end-1)
                start = end + 1
                end += 1

        reverse(s, 0, len(s)-1)
        reverse_word(s)
