/* Write your T-SQL query statement below */
select  item_category  as Category, isnull([Monday],0) as Monday, isnull([Tuesday],0) as Tuesday, 
    isnull([Wednesday],0) as Wednesday, isnull([Thursday],0) as Thursday, isnull([Friday],0) as Friday, 
    isnull([Saturday],0) as Saturday, isnull([Sunday],0) as Sunday
from (
    select i. item_category , datename(weekday, order_date) as day,
    sum(o.quantity) as total_quantity
    from Orders o 
    full outer join Items i on o.item_id = i.item_id
    group by i. item_category , datename(weekday, order_date)
) AS SourceTable
Pivot
(
    sum(total_quantity)
    for day in ([Monday], [Tuesday], [Wednesday], [Thursday], [Friday], [Saturday], [Sunday])
) AS PivotTable;
