class Solution {
public:
    bool areSentencesSimilar(vector<string>& sentence1, vector<string>& sentence2, vector<vector<string>>& similarPairs) {

        map<string,set<string>> msim;

        for(int i = 0; i< similarPairs.size(); i++)
        {
            msim[similarPairs[i][0]].insert(similarPairs[i][1]);
            msim[similarPairs[i][1]].insert(similarPairs[i][0]);
        }

        if(sentence1.size() != sentence2.size())
            return false;

        for(int i = 0; i< sentence1.size(); i++)
        {
            if(sentence1[i] == sentence2[i])
                continue;
            if(msim[sentence1[i]].size() && 
               msim[sentence1[i]].find(sentence2[i]) != msim[sentence1[i]].end() )
               continue; 

            if(msim[sentence2[i]].size() && 
               msim[sentence2[i]].find(sentence1[i]) != msim[sentence2[i]].end() )
               continue; 
            return false;
        }

        return true;

    }
};
