class Solution {
public:
    string shortestBeautifulSubstring(string s, int k) {
        string ans = "";
        int len = 1e9;
        for(int i = 0; i < s.length(); i++){
            int c =  0;
            string temp = "";
            for(int j = i; j < s.length(); j++){
                temp.push_back(s[j]);
                if(s[j] == '1')c++;
                if(c == k){
                    if((j - i + 1) < len){
                        len = (j - i + 1);
                        ans = temp;
                    }else if((j - i + 1) == len){
                        if(ans > temp){
                            ans = temp;
                        }
                    }
                }
            }
        }
        return ans;
    }
};
