class Solution:
    def shortestDistanceColor(self, colors: List[int], queries: List[List[int]]) -> List[int]:
        n = len(colors)
        arr = [[None] * n for _ in range(4)]
        last = {}
        for i in range(n):
            last[colors[i]] = i
            for j in range(1, 4):
                if j in last:
                    arr[j][i] = (i - last[j], last[j])
        last = {}
        for i in range(n - 1, -1, -1):
            last[colors[i]] = i
            for j in range(1, 4):
                if j not in last:
                    continue
                if not arr[j][i]:
                    arr[j][i] = (last[j] - i,  last[j])
                    continue

                if last[j] - i < arr[j][i][0]:
                    arr[j][i] = (last[j] - i,  last[j])


        res = []
        for i, c in queries:
            if arr[c][i]:
                res.append(arr[c][i][0])
            else:
                res.append(-1)
        return res
