class Solution {
public:
    int findLengthOfShortestSubarray(vector<int>& arr) { int n=arr.size();
        if(n==1) return 0;
        int l=0,r=n-1; vector<int>a,b;
        a.push_back(arr[0]); b.push_back(arr[n-1]);
        for(int i=1;i<arr.size();i++)
        {
            if(arr[i]>=arr[i-1])
            {a.push_back(arr[i]);l=i;}
            else
                break;
        }
        if(l==n-1) {return 0;}
           for(int i=n-2;i>=0;i--)
        {
            if(arr[i]<=arr[i+1])
            {b.push_back(arr[i]);r=i;}
            else
                break;
        }
      reverse(b.begin(),b.end());
     //  cout<<l<<" "<<r<<endl;
        int ans=INT_MAX,zz=a.size();
      for(int i=0;i<a.size();i++)
        {  
            int p=lower_bound(b.begin(),b.end(),a[i])-b.begin();
         // cout<<a[i]<<" "<<p<<endl;
            ans=min(ans,p+zz-1-i);
        }  
             ans=min(ans,zz);
   ans+=r-l-1;
                  return ans;
    }
};
